import { useState } from 'react';
import ExpenseItem from './components/Expenses/ExpenseItem';
import NewExpense from './components/NewExpense/NewExpense';

function App() {
  const [Expenses,setExpenses] = useState([
    {name:"Keeway C200 MotorCycle", Date: new Date().toISOString(),price:1000,checked:false},
    {name:"macbook pro", Date:"20 aug 2022",price:500},
    {name:"Peugeut 406", Date:"20 aug 2052",price:2000},
  ])
  const handleNewExpense = (expenseData) => {
    setExpenses( (previousExpenses => previousExpenses.push(expenseData)))
  }
  return (
    <div className="bg-slate-800 w-screen h-screen m-0 absolute px-4">
      <NewExpense onSaveExpense={handleNewExpense}/>
      {Expenses.map( exp => <ExpenseItem name={exp.name} date={exp.Date} price={exp.price} checked={exp.checked}></ExpenseItem>)}
    </div>
  );
}

export default App;
