import { useState } from "react";

function NewExpense(props) {
    const [inputs,setInputs] = useState({})
    setInputs({
        name:"",
        date:"",
        price:""
    })
    const handleChangeDate = (e) => {
        setInputs( (prev) => {
            return {
                ...prev,
                date:e.target.value
            }
        })
    }
    const handleSubmit = (e)  => {
        e.perventDefault()
        console.log(e.target)
    }
  return (
    <div className="max-w-xl mx-auto  bg-sky-800 p-5 m-4 rounded-lg text-sky-50 font-bold align-middle">
      <form className="flex gap-5 w-full flex-wrap mb-2" onSubmit={handleSubmit}>
        <div className="w-full mb-1 flex">
        <h1 className="flex-1 "> New Expense </h1>
        <h1 className="w-5 ">-</h1>
        </div>
        <div className="flex">
          <label className="w-16 mt-1">date</label>
          <input type="date" onChange={handleChangeDate} className="rounded-md bg-sky-100 text-sky-900  px-2 py-1 w-30"  />
        </div>
        <div className="flex">
          <label className="w-16 mt-1">name</label>
          <input type="text" className="rounded-md bg-sky-100 text-sky-900  px-2 py-1 w-30 " />
        </div>
        <div className="flex">
          <label className="w-16 mt-1">price</label>
          <input type="number" min={0.1} step={0.1}  className="rounded-md bg-sky-100 text-sky-900 px-2 py-1   "  />
        </div>
        <button type="submit"  className="bg-sky-500 hover:text-sky-950 py-1 px-4 rounded-md hover:bg-sky-400">Confirm</button>
      </form>
    </div>
  );
}

export default NewExpense;
